DROP TABLE IF EXISTS `trade`;
CREATE TABLE `trade` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `order_no` varchar(255) NOT NULL DEFAULT '' COMMENT '平台订单号',
  `company_no` varchar(255) NOT NULL DEFAULT '' COMMENT '商户编号',
  `company_order_no` varchar(255) NOT NULL DEFAULT '' COMMENT '商户订单号',
  `apply_amount` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '申请金额',
  `actual_amount` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '到账金额',
  `service_fee` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商户手续费',
  `order_status` tinyint(4) NOT NULL COMMENT '订单状态:1:processing 2:success 3:overtime',
  `arrive_time` timestamp NULL DEFAULT NULL COMMENT '到账时间',
  `push_time` timestamp NULL DEFAULT '0000-00-00 00:00:00' COMMENT '回调时间',
  `push_status` tinyint(4) DEFAULT NULL COMMENT '回调状态',
  `crt_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `upt_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后一次更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

